      <Image src={require(`../../Images/${photo}.png`).default} preview={false} height={200} width={200}></Image>
      <br/>
      </Image.PreviewGroup>
      {/* <p>{photo}</p> */}
    </div>
    </div>
    </div>
  );
};

export default Animals;
