import Home from "../Home/Home";
// import Link from "antd/lib/typography/Link";

const Fruits = () => {

let fruitsAndVegetables = ["Apple", "Banana", "Grapes", "Mango", "Orange", "Papaya", "Watermelon", "Guava", "PineApple"];
